import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Button } from './UI';
import sdaLogo from '../assets/sda_logo.png';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const location = useLocation();

  const departments = [
    { name: 'Sabbath School', path: '/departments/sabbath-school', icon: 'ðŸ“–' },
    { name: 'AY & Pathfinder', path: '/departments/ay-pathfinder', icon: 'ðŸ•ï¸' },
    { name: 'Children\'s Ministries', path: '/departments/childrens-ministries', icon: 'ðŸ‘¶' },
    { name: 'Communication', path: '/departments/communication', icon: 'ðŸ“¢' },
    { name: 'Health Ministries', path: '/departments/health-ministries', icon: 'ðŸ¥' },
    { name: 'Family Ministries', path: '/departments/family-ministries', icon: 'ðŸ‘¨â€ðŸ‘©â€ðŸ‘§â€ðŸ‘¦' },
    { name: 'PARL', path: '/departments/parl', icon: 'âš–ï¸' },
    { name: 'Treasury', path: '/departments/treasury', icon: 'ðŸ’°' }
  ];

  const navStyle = {
    backgroundColor: '#2c3e50',
    color: 'white',
    padding: '1rem 2rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexWrap: 'wrap',
    gap: '1rem'
  };

  const linkStyle = (isActive) => ({
    color: isActive ? '#3498db' : 'white',
    textDecoration: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    backgroundColor: isActive ? 'rgba(52, 152, 219, 0.1)' : 'transparent',
    transition: 'all 0.2s',
    whiteSpace: 'nowrap'
  });

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <header style={navStyle}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '2rem', flexWrap: 'wrap' }}>
          <Link to="/dashboard" style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', textDecoration: 'none' }}>
            <img src={sdaLogo} alt="Nyamasare SDA Church" style={{ maxHeight: '40px', height: 'auto' }} />
            <span style={{ ...linkStyle(location.pathname === '/dashboard'), fontSize: '1.2rem', fontWeight: '700', color: 'white' }}>NYAMASARE SDA CHURCH</span>
          </Link>
          <nav style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
            <Link to="/dashboard" style={linkStyle(location.pathname === '/dashboard')}>Dashboard</Link>
            {departments.map(dept => (
              <Link
                key={dept.path}
                to={dept.path}
                style={linkStyle(location.pathname === dept.path)}
                title={dept.name}
              >
                {dept.icon} {dept.name}
              </Link>
            ))}
          </nav>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <span>{user?.first_name} ({user?.role})</span>
          <Button variant="danger" size="small" onClick={logout}>Logout</Button>
        </div>
      </header>

      {/* Main Content */}
      <main style={{ flex: 1, backgroundColor: '#f8f9fa' }}>
        {children}
      </main>

      {/* Footer */}
      <footer style={{ backgroundColor: '#34495e', color: 'white', padding: '1rem 2rem', textAlign: 'center' }}>
        <p>&copy; 2024 SDA Church Portal. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Layout;
